create table admin_recharges
(
    id                              int unsigned auto_increment
        primary key,
    user_id                         int unsigned                           not null comment '充值对象',
    recharge_type                   tinyint      default 0                 not null comment '充值类型:0 增加，1 减少',
    recharge_balance_in_cents       int unsigned default 0                 not null comment '充值账户余额',
    recharge_withdraw_in_cents      int unsigned default 0                 not null comment '充值提现余额',
    recharge_redemption_points      int unsigned default 0                 not null comment '充值复购积分',
    recharge_raffle_tickets         int unsigned default 0                 not null comment '充值抽奖券张数',
    one_raffle_tickets_worth_points int unsigned                           not null comment '每张抽奖券的面值（值多少积分）',
    comment                         text                                   not null comment '备注',
    reviewer_id                     int unsigned default 0                 not null comment '后台充值处理人ID',
    created_at                      datetime     default CURRENT_TIMESTAMP not null,
    updated_at                      datetime                               null,
    deleted_at                      datetime                               null
)
    collate = utf8mb4_unicode_ci;

create index admin_recharges_recharge_type_index
    on admin_recharges (recharge_type);

create index admin_recharges_user_id_index
    on admin_recharges (user_id);

