create table balance_summaries
(
    id                          int unsigned auto_increment
        primary key,
    store_id                    int unsigned                              not null comment '所属店铺（变更记录本身关联店铺或用户的分销店铺）',
    account_balance_in_cents    bigint unsigned default 0                 not null comment '账户余额',
    withdrawal_balance_in_cents bigint unsigned default 0                 not null comment '提现余额',
    redemption_point            bigint unsigned default 0                 not null comment '复购积分',
    raffle_used_point           bigint unsigned default 0                 not null comment '抽奖消耗积分',
    redemption_used_point       bigint unsigned default 0                 not null comment '复购消耗积分',
    withdrawn_balance_in_cents  bigint unsigned default 0                 not null comment '已提现金额',
    expense_in_cents            bigint unsigned default 0                 not null comment '购物金额',
    used_point                  bigint unsigned default 0                 not null comment '消耗积分',
    summary_date                datetime                                  not null comment '统计区间（按天统计，值为当天的0时0分0秒）',
    created_at                  datetime        default CURRENT_TIMESTAMP not null,
    updated_at                  datetime                                  null
)
    collate = utf8mb4_unicode_ci;

create index balance_summaries_store_id_index
    on balance_summaries (store_id);

create index balance_summaries_summary_date_index
    on balance_summaries (summary_date);

