create table products
(
    id                        int unsigned auto_increment comment '商品ID'
        primary key,
    source_id                 int unsigned default 0                 not null comment '该项数据的拷贝来源，店铺的商品数据为同类平台数据的部分拷贝',
    store_id                  int unsigned default 0                 not null comment '店铺ID',
    name                      varchar(128)                           not null comment '商品名称',
    weight                    int unsigned                           not null comment '权重',
    keywords                  text                                   not null comment '搜索关键词',
    category_id               int unsigned default 0                 not null comment '分类ID',
    category_name             varchar(32)                            not null comment '分类名称',
    category_name_abbr        varchar(32)                            not null comment '分类名称简称',
    category_display_order    int unsigned default 0                 not null comment '商品分类排序',
    marketing_rule            tinyint      default 0                 not null comment '营销规则，0：活动专区、1：平价区、2：复购区',
    price_in_cents            int unsigned                           not null comment '价格（分）',
    required_redemption_point int unsigned default 0                 not null comment '复购商品所需的复购积分',
    cost_price_in_cents       int unsigned                           not null comment '成本价（分）',
    has_triple_points         tinyint(1)                             not null comment '购买时是否赠送三倍积分',
    has_raffle_ticket         tinyint(1)                             not null comment '购买时是否赠送抽奖券',
    cover_image_id            int unsigned                           not null comment '封面图的文件ID',
    cover_image_path          varchar(255)                           not null comment '封面图的文件相对路径',
    cover_image_url           varchar(255)                           not null comment '封面图的文件URL',
    sales                     int unsigned                           not null comment '已销售数量',
    is_sales_visible          tinyint(1)                             not null comment '是否显示销量',
    number_of_customers       int unsigned default 0                 not null comment '购买人数',
    stock_count               int unsigned                           not null comment '库存总量',
    is_for_sale               tinyint(1)                             not null comment '是否已上架销售',
    reason_for_not_for_sale   text                                   not null comment '下架理由',
    shipping_cost_in_cents    int unsigned                           not null comment '邮费',
    is_free_shipping          tinyint(1)                             not null comment '是否包邮',
    is_recommended            tinyint(1)                             not null comment '是否是推荐商品',
    created_at                datetime     default CURRENT_TIMESTAMP not null,
    updated_at                datetime                               null,
    deleted_at                datetime                               null
)
    collate = utf8mb4_unicode_ci;

create fulltext index products_keywords_fulltext
    on products (keywords);

create index products_source_id_index
    on products (source_id);

create index products_store_id_index
    on products (store_id);

