create table user_activity_orders
(
    id                    int unsigned auto_increment comment '用户活动区首单记录ID'
        primary key,
    product_id            int unsigned                              not null comment '商品ID',
    order_id              int unsigned                              not null comment '订单ID',
    user_id               int unsigned                              not null comment '人员ID',
    total_amount_in_cents bigint unsigned default 0                 not null comment '活动区订单的累计金额（持续更新）',
    created_at            datetime        default CURRENT_TIMESTAMP not null,
    updated_at            datetime                                  null
)
    collate = utf8mb4_unicode_ci;

create index user_first_activity_orders_user_id_index
    on user_activity_orders (user_id);

